contracts/adapter/GnosisAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IVPOAdapter} from "../interfaces/IVPOAdapter.sol";
import {IGnosisConditionalTokens} from "../interfaces/IGnosisConditionalTokens.sol";
import {Errors} from "../security/Errors.sol";

/// @title Gnosis Adapter
/// @notice Bridges Gnosis Conditional Tokens with VPOAdapter for verifiable outcomes
/// @dev Listens to Gnosis condition events, requests verification from VPOAdapter, and sets outcomes
contract GnosisAdapter {
	/// @notice The VPOAdapter contract
	IVPOAdapter public immutable vpoAdapter;

	/// @notice The Gnosis ConditionalTokens contract
	IGnosisConditionalTokens public immutable conditionalTokens;

	/// @notice Admin address
	address public admin;

	/// @notice Mapping from condition ID to VPOAdapter request ID
	mapping(bytes32 => bytes32) public conditionToRequest;

	/// @notice Mapping from VPOAdapter request ID to condition ID
	mapping(bytes32 => bytes32) public requestToCondition;

	/// @notice Mapping to track if a condition has been resolved
	mapping(bytes32 => bool) public conditionResolved;

	modifier onlyAdmin() {
		if (msg.sender != admin) revert Errors.OnlyAdmin();
		_;
	}

	constructor(
		address vpoAdapter_,
		address conditionalTokens_,
		address admin_
	) {
		if (vpoAdapter_ == address(0)) revert Errors.ZeroAddress();
		if (conditionalTokens_ == address(0)) revert Errors.ZeroAddress();
		if (admin_ == address(0)) revert Errors.ZeroAddress();

		vpoAdapter = IVPOAdapter(vpoAdapter_);
		conditionalTokens = IGnosisConditionalTokens(conditionalTokens_);
		admin = admin_;
	}

	/// @notice Handle a new Gnosis condition by requesting verification from VPOAdapter
	/// @param conditionId The Gnosis condition ID
	/// @param questionId The question ID (bytes32 identifier)
	/// @param outcomeSlotCount The number of outcome slots
	/// @param data Additional data for VPOAdapter (data sources, timestamps, etc.)
	/// @return requestId The VPOAdapter request ID
	function handleCondition(
		bytes32 conditionId,
		bytes32 questionId,
		uint256 outcomeSlotCount,
		bytes calldata data
	) external returns (bytes32 requestId) {
		// Check condition is not already resolved
		(bool resolved, ) = conditionalTokens.getConditionPayouts(conditionId);
		if (resolved) revert Errors.AlreadyFulfilled();

		if (conditionToRequest[conditionId] != bytes32(0)) {
			revert Errors.AlreadyFulfilled(); // Already handled
		}

		// Request verification from VPOAdapter
		// Use conditionId as marketRef
		requestId = vpoAdapter.requestVerification(conditionId, data);

		// Store mapping
		conditionToRequest[conditionId] = requestId;
		requestToCondition[requestId] = conditionId;

		emit ConditionHandled(conditionId, requestId, questionId, outcomeSlotCount);
	}

	/// @notice Resolve a condition after VPOAdapter fulfillment
	/// @param requestId The VPOAdapter request ID
	/// @param outcomeSlotCount The number of outcome slots (must match condition)
	/// @return conditionId The Gnosis condition ID
	function resolveCondition(
		bytes32 requestId,
		uint256 outcomeSlotCount
	) external onlyAdmin returns (bytes32 conditionId) {
		// Get the condition ID
		conditionId = requestToCondition[requestId];
		if (conditionId == bytes32(0)) revert Errors.NotFound();

		// Check VPOAdapter fulfillment
		(bool exists, , bool outcome, ) = vpoAdapter.getFulfillment(requestId);
		if (!exists) revert Errors.NotFound();

		// Check if already resolved
		if (conditionResolved[conditionId]) revert Errors.AlreadyFulfilled();

		// Verify condition is not already resolved on Gnosis side
		(bool resolved, ) = conditionalTokens.getConditionPayouts(conditionId);
		if (resolved) {
			conditionResolved[conditionId] = true;
			emit ConditionResolved(conditionId, requestId, outcome);
			return conditionId;
		}

		// Create payouts array
		// For binary markets: outcomeSlotCount should be 2
		// outcome = true means slot 0 wins (YES), outcome = false means slot 1 wins (NO)
		uint256[] memory payouts = new uint256[](outcomeSlotCount);

		if (outcome) {
			// YES wins: slot 0 gets full payout
			payouts[0] = 1;
			for (uint256 i = 1; i < outcomeSlotCount; i++) {
				payouts[i] = 0;
			}
		} else {
			// NO wins: slot 1 gets full payout (or last slot if more than 2)
			payouts[0] = 0;
			if (outcomeSlotCount > 1) {
				payouts[outcomeSlotCount - 1] = 1;
				for (uint256 i = 1; i < outcomeSlotCount - 1; i++) {
					payouts[i] = 0;
				}
			}
		}

		// Report payouts to Gnosis
		conditionalTokens.reportPayouts(conditionId, payouts);

		// Mark as resolved
		conditionResolved[conditionId] = true;

		emit ConditionResolved(conditionId, requestId, outcome);
	}

	/// @notice Get the VPOAdapter request ID for a Gnosis condition
	/// @param conditionId The Gnosis condition ID
	/// @return requestId The VPOAdapter request ID (bytes32(0) if not found)
	function getRequestId(bytes32 conditionId) external view returns (bytes32 requestId) {
		return conditionToRequest[conditionId];
	}

	/// @notice Get the Gnosis condition ID for a VPOAdapter request
	/// @param requestId The VPOAdapter request ID
	/// @return conditionId The Gnosis condition ID (bytes32(0) if not found)
	function getConditionId(bytes32 requestId) external view returns (bytes32 conditionId) {
		return requestToCondition[requestId];
	}

	/// @notice Event emitted when a Gnosis condition is handled
	event ConditionHandled(
		bytes32 indexed conditionId,
		bytes32 indexed requestId,
		bytes32 questionId,
		uint256 outcomeSlotCount
	);

	/// @notice Event emitted when a condition is resolved
	event ConditionResolved(
		bytes32 indexed conditionId,
		bytes32 indexed requestId,
		bool outcome
	);
}

contracts/adapter/UMAAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IVPOAdapter} from "../interfaces/IVPOAdapter.sol";
import {IUMAOptimisticOracle} from "../interfaces/IUMAOptimisticOracle.sol";
import {Errors} from "../security/Errors.sol";

/// @title UMA Adapter
/// @notice Bridges UMA Optimistic Oracle assertions with VPOAdapter for verifiable outcomes
/// @dev Listens to UMA assertions, requests verification from VPOAdapter, and submits outcomes back to UMA
contract UMAAdapter {
	/// @notice The VPOAdapter contract
	IVPOAdapter public immutable vpoAdapter;

	/// @notice The UMA Optimistic Oracle contract
	IUMAOptimisticOracle public immutable umaOracle;

	/// @notice Admin address
	address public admin;

	/// @notice Mapping from UMA assertion ID to VPOAdapter request ID
	mapping(bytes32 => bytes32) public assertionToRequest;

	/// @notice Mapping from VPOAdapter request ID to UMA assertion ID
	mapping(bytes32 => bytes32) public requestToAssertion;

	/// @notice Mapping to track if an assertion has been submitted to UMA
	mapping(bytes32 => bool) public assertionSubmitted;

	modifier onlyAdmin() {
		if (msg.sender != admin) revert Errors.OnlyAdmin();
		_;
	}

	constructor(address vpoAdapter_, address umaOracle_, address admin_) {
		if (vpoAdapter_ == address(0)) revert Errors.ZeroAddress();
		if (umaOracle_ == address(0)) revert Errors.ZeroAddress();
		if (admin_ == address(0)) revert Errors.ZeroAddress();

		vpoAdapter = IVPOAdapter(vpoAdapter_);
		umaOracle = IUMAOptimisticOracle(umaOracle_);
		admin = admin_;
	}

	/// @notice Handle a new UMA assertion by requesting verification from VPOAdapter
	/// @param assertionId The UMA assertion ID
	/// @param claim The claim being asserted (encoded question/data)
	/// @param data Additional data for VPOAdapter (data sources, timestamps, etc.)
	/// @return requestId The VPOAdapter request ID
	function handleAssertion(
		bytes32 assertionId,
		bytes calldata claim,
		bytes calldata data
	) external returns (bytes32 requestId) {
		// Check assertion exists and is not already handled
		(, , , , , , , , bool settled, ) = umaOracle.getAssertion(assertionId);
		if (settled) revert Errors.AlreadyFulfilled();

		if (assertionToRequest[assertionId] != bytes32(0)) {
			revert Errors.AlreadyFulfilled(); // Already handled
		}

		// Request verification from VPOAdapter
		// Use assertionId as marketRef
		requestId = vpoAdapter.requestVerification(assertionId, data);

		// Store mapping
		assertionToRequest[assertionId] = requestId;
		requestToAssertion[requestId] = assertionId;

		emit AssertionHandled(assertionId, requestId, claim);
	}

	/// @notice Submit verified outcome to UMA after VPOAdapter fulfillment
	/// @param requestId The VPOAdapter request ID
	/// @param claim The claim to assert (should match original UMA assertion claim)
	/// @param liveness The liveness period for disputes (in seconds)
	/// @param currency The currency for bonds (address(0) for native)
	/// @param bond The bond amount (can be 0)
	/// @param identifier The identifier for the assertion
	/// @return assertionId The UMA assertion ID (may be new or existing)
	function submitOutcomeToUMA(
		bytes32 requestId,
		bytes calldata claim,
		uint64 liveness,
		address currency,
		uint256 bond,
		bytes32 identifier
	) external onlyAdmin returns (bytes32 assertionId) {
		// Get the original assertion ID
		assertionId = requestToAssertion[requestId];
		if (assertionId == bytes32(0)) revert Errors.NotFound();

		// Check VPOAdapter fulfillment
		(bool exists, , bool outcome, ) = vpoAdapter.getFulfillment(requestId);
		if (!exists) revert Errors.NotFound();

		// Check if already submitted
		if (assertionSubmitted[assertionId]) revert Errors.AlreadyFulfilled();

		// Check if original assertion is already settled
		bool settled = _isAssertionSettled(assertionId);
		if (settled) {
			bool settlementResolution = _getSettlementResolution(assertionId);
			assertionSubmitted[assertionId] = true;
			emit OutcomeSubmitted(assertionId, requestId, settlementResolution);
			return assertionId;
		}

		// Create new assertion with verified outcome
		bytes memory outcomeClaim = abi.encode(claim, outcome);
		bytes32 newAssertionId = _createUMAAssertion(
			outcomeClaim,
			liveness,
			currency,
			bond,
			identifier
		);

		// Mark as submitted
		assertionSubmitted[assertionId] = true;

		emit OutcomeSubmitted(assertionId, requestId, outcome);
		emit UMAAssertionCreated(newAssertionId, assertionId, outcome);
	}

	/// @dev Check if an assertion is settled
	function _isAssertionSettled(bytes32 assertionId) internal view returns (bool) {
		(, , , , , , , , bool settled, ) = umaOracle.getAssertion(assertionId);
		return settled;
	}

	/// @dev Get settlement resolution
	function _getSettlementResolution(bytes32 assertionId) internal view returns (bool) {
		(, , , , , , , , , bool settlementResolution) = umaOracle.getAssertion(assertionId);
		return settlementResolution;
	}

	/// @dev Create UMA assertion
	function _createUMAAssertion(
		bytes memory outcomeClaim,
		uint64 liveness,
		address currency,
		uint256 bond,
		bytes32 identifier
	) internal returns (bytes32) {
		return umaOracle.assertTruth(
			outcomeClaim,
			address(this), // asserter
			address(0), // callbackRecipient
			address(0), // escalationManager
			liveness,
			currency,
			bond,
			identifier
		);
	}

	/// @notice Get the VPOAdapter request ID for a UMA assertion
	/// @param assertionId The UMA assertion ID
	/// @return requestId The VPOAdapter request ID (bytes32(0) if not found)
	function getRequestId(bytes32 assertionId) external view returns (bytes32 requestId) {
		return assertionToRequest[assertionId];
	}

	/// @notice Get the UMA assertion ID for a VPOAdapter request
	/// @param requestId The VPOAdapter request ID
	/// @return assertionId The UMA assertion ID (bytes32(0) if not found)
	function getAssertionId(bytes32 requestId) external view returns (bytes32 assertionId) {
		return requestToAssertion[requestId];
	}

	/// @notice Event emitted when a UMA assertion is handled
	event AssertionHandled(
		bytes32 indexed assertionId,
		bytes32 indexed requestId,
		bytes claim
	);

	/// @notice Event emitted when an outcome is submitted to UMA
	event OutcomeSubmitted(
		bytes32 indexed assertionId,
		bytes32 indexed requestId,
		bool outcome
	);

	/// @notice Event emitted when a new UMA assertion is created
	event UMAAssertionCreated(
		bytes32 indexed newAssertionId,
		bytes32 indexed originalAssertionId,
		bool outcome
	);
}

contracts/adapter/VPOAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IVPOAdapter} from "../interfaces/IVPOAdapter.sol";
import {Errors} from "../security/Errors.sol";

/// @title VPO Adapter
/// @notice On-chain adapter that receives verification requests from external prediction markets
/// and coordinates with EigenCloud AVS nodes to provide verifiable outcomes.
contract VPOAdapter is IVPOAdapter {
	/// @dev requestId => Request
	mapping(bytes32 => Request) private _requests;

	/// @dev AVS nodes that are authorized to fulfill requests
	mapping(address => bool) public avsNodes;

	address public admin;

	modifier onlyAdmin() {
		if (msg.sender != admin) revert Errors.OnlyAdmin();
		_;
	}

	modifier onlyAVS() {
		if (!avsNodes[msg.sender]) revert Errors.Unauthorized();
		_;
	}

	constructor(address admin_) {
		if (admin_ == address(0)) revert Errors.ZeroAddress();
		admin = admin_;
	}

	/// @notice Add or remove an AVS node
	function setAVSNode(address node, bool enabled) external onlyAdmin {
		if (node == address(0)) revert Errors.ZeroAddress();
		avsNodes[node] = enabled;
		emit AVSNodeUpdated(node, enabled);
	}

	/// @notice Request verification for an external market
	/// @param marketRef Identifier from external market (e.g., UMA dispute id, conditional question id)
	/// @param data Encoded parameters (data sources, timestamps, query details)
	/// @return requestId Unique identifier for this request
	function requestVerification(bytes32 marketRef, bytes calldata data)
		external
		override
		returns (bytes32 requestId)
	{
		// Generate unique request ID
		requestId = keccak256(abi.encodePacked(marketRef, msg.sender, block.timestamp, data, block.prevrandao));

		// Store request
		_requests[requestId] = Request({
			marketRef: marketRef,
			requester: msg.sender,
			data: data,
			fulfilled: false,
			attestationCid: "",
			outcome: false,
			metadata: ""
		});

		emit VerificationRequested(requestId, msg.sender, marketRef, data);
	}

	/// @notice Fulfill a verification request with attestation and outcome
	/// @param requestId The request ID from requestVerification
	/// @param attestationCid IPFS CID (as bytes) to the public proof payload
	/// @param outcome Resolved boolean outcome (true = YES, false = NO)
	/// @param metadata Provider-specific metadata (timestamps, signatures, etc.)
	function fulfillVerification(
		bytes32 requestId,
		bytes calldata attestationCid,
		bool outcome,
		bytes calldata metadata
	) external override onlyAVS {
		Request storage req = _requests[requestId];

		// Check request exists
		if (req.requester == address(0)) revert Errors.NotFound();

		// Check not already fulfilled
		if (req.fulfilled) revert Errors.AlreadyFulfilled();

		// Update request
		req.fulfilled = true;
		req.attestationCid = attestationCid;
		req.outcome = outcome;
		req.metadata = metadata;

		emit VerificationFulfilled(requestId, attestationCid, outcome, metadata);
	}

	/// @notice Read back fulfillment if present
	/// @param requestId The request ID to check
	/// @return exists Whether fulfillment exists
	/// @return attestationCid IPFS CID bytes
	/// @return outcome Resolved boolean
	/// @return metadata Opaque metadata
	function getFulfillment(bytes32 requestId)
		external
		view
		override
		returns (bool exists, bytes memory attestationCid, bool outcome, bytes memory metadata)
	{
		Request memory req = _requests[requestId];

		if (req.requester == address(0)) {
			return (false, "", false, "");
		}

		if (!req.fulfilled) {
			return (false, "", false, "");
		}

		return (true, req.attestationCid, req.outcome, req.metadata);
	}

	/// @notice Get request details (for debugging/verification)
	function getRequest(bytes32 requestId) external view returns (Request memory) {
		return _requests[requestId];
	}

	// Internal struct
	struct Request {
		bytes32 marketRef;
		address requester;
		bytes data;
		bool fulfilled;
		bytes attestationCid;
		bool outcome;
		bytes metadata;
	}

	/// @notice Event emitted when AVS node is added/removed
	event AVSNodeUpdated(address indexed node, bool enabled);
}

