// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

library PayoutCalculator {
	/// @notice Compute payouts for long/short shares in a binary market.
	/// @param outcome 0 = short wins, 1 = long wins.
	/// @param longShares User's long share balance.
	/// @param shortShares User's short share balance.
	/// @return payout Amount of collateral to redeem.
	function computeBinary(uint8 outcome, uint256 longShares, uint256 shortShares)
		internal
		pure
		returns (uint256 payout)
	{
		if (outcome == 0) {
			return shortShares;
		} else if (outcome == 1) {
			return longShares;
		}
		return 0;
	}
}

contracts/market/Market.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {IVPOOracle} from "../interfaces/IVPOOracle.sol";
import {PayoutCalculator} from "../libs/PayoutCalculator.sol";
import {Errors} from "../security/Errors.sol";
import {Vault} from "./Vault.sol";

/// @notice Binary prediction market with escrowed collateral and oracle resolution.
contract Market is ReentrancyGuard, Pausable {
	using PayoutCalculator for uint256;

	enum Status { Trading, PendingResolve, Resolved }

	IERC20 public immutable collateral;
	IVPOOracle public immutable oracle;
	address public immutable factory;
	Vault public immutable vault;

	bytes32 public immutable marketId; // derived by factory
	string public question;
	uint256 public endTime; // trading cutoff
	uint16 public feeBps; // reserved (unused for now)
	uint256 public flatFee; // fixed amount in collateral smallest units (e.g., 0.5 USDC = 500000)
	address public feeRecipient;

	Status public status;
	uint8 public outcome; // 0 = short, 1 = long

	// Simplified share balances per user for long/short
	mapping(address => uint256) public longOf;
	mapping(address => uint256) public shortOf;

	event MarketCreated(bytes32 indexed marketId, string question, uint256 endTime, address oracle);
	event Trade(address indexed trader, bool isLong, uint256 collateralInOrOut, uint256 sharesDelta, uint256 fee);
	event CloseTrading(bytes32 indexed marketId);
	event Resolve(bytes32 indexed marketId, uint8 outcome, bytes resultData, bytes metadata);
	event Redeem(address indexed user, uint256 payout);

	modifier onlyFactory() {
		if (msg.sender != factory) revert Errors.OnlyFactory();
		_;
	}

	constructor(
		address collateral_,
		address oracle_,
		address factory_,
		address vault_,
		bytes32 marketId_,
		string memory question_,
		uint256 endTime_,
		uint16 feeBps_,
		uint256 flatFee_,
		address feeRecipient_
	) {
		if (collateral_ == address(0) || oracle_ == address(0) || factory_ == address(0) || vault_ == address(0)) {
			revert Errors.ZeroAddress();
		}
		if (bytes(question_).length == 0) revert Errors.InvalidParameter();
		if (endTime_ <= block.timestamp) revert Errors.InvalidTime();
		if (feeBps_ > 10_000) revert Errors.InvalidFee();
		if (feeRecipient_ == address(0)) revert Errors.ZeroAddress();

		collateral = IERC20(collateral_);
		oracle = IVPOOracle(oracle_);
		factory = factory_;
		vault = Vault(vault_);
		marketId = marketId_;
		question = question_;
		endTime = endTime_;
		feeBps = feeBps_;
		flatFee = flatFee_;
		feeRecipient = feeRecipient_;

		status = Status.Trading;
		emit MarketCreated(marketId, question_, endTime_, oracle_);
	}

	function buy(bool isLong, uint256 collateralIn) external nonReentrant {
		if (status != Status.Trading) revert Errors.TradingClosed();
		if (block.timestamp >= endTime) revert Errors.TradingClosed();
		if (collateralIn <= flatFee) revert Errors.InvalidParameter();

		uint256 fee = flatFee;
		uint256 amountNet = collateralIn - fee;

		// Pull collateral from trader to vault
		vault.depositFrom(msg.sender, collateralIn);
		// Pay fee from trader escrow to feeRecipient
		vault.withdrawFromTo(msg.sender, feeRecipient, fee);

		// Mint shares 1:1 to net collateral
		if (isLong) {
			longOf[msg.sender] += amountNet;
		} else {
			shortOf[msg.sender] += amountNet;
		}
		emit Trade(msg.sender, isLong, collateralIn, amountNet, fee);
	}

	function sell(bool isLong, uint256 shares) external nonReentrant {
		if (status != Status.Trading) revert Errors.TradingClosed();
		if (block.timestamp >= endTime) revert Errors.TradingClosed();
		if (shares <= flatFee) revert Errors.InvalidParameter();

		uint256 fee = flatFee;
		uint256 amountNet = shares - fee;

		if (isLong) {
			uint256 bal = longOf[msg.sender];
			if (bal < shares) revert Errors.InsufficientBalance();
			longOf[msg.sender] = bal - shares;
		} else {
			uint256 bal = shortOf[msg.sender];
			if (bal < shares) revert Errors.InsufficientBalance();
			shortOf[msg.sender] = bal - shares;
		}

		// Fee sent to recipient, net returned to trader, both from trader escrow
		vault.withdrawFromTo(msg.sender, feeRecipient, fee);
		vault.withdrawFromTo(msg.sender, msg.sender, amountNet);

		emit Trade(msg.sender, isLong, amountNet, shares, fee);
	}

	function closeTrading() external {
		if (block.timestamp < endTime) revert Errors.TradingOpen();
		if (status != Status.Trading) revert Errors.InvalidParameter();
		status = Status.PendingResolve;
		emit CloseTrading(marketId);
	}

	function requestResolve(bytes calldata extraData) external {
		if (status != Status.PendingResolve) revert Errors.InvalidParameter();
		oracle.requestResolve(marketId, extraData);
	}

	function settleFromOracle() external nonReentrant {
		if (status != Status.PendingResolve) revert Errors.InvalidParameter();
		(bool resolved, bytes memory resultData, bytes memory metadata) = oracle.getResult(marketId);
		if (!resolved) revert Errors.MarketNotResolved();
		// Expect resultData to be abi.encode(uint8 outcome)
		uint8 o = abi.decode(resultData, (uint8));
		if (o > 1) revert Errors.InvalidParameter();
		outcome = o;
		status = Status.Resolved;
		emit Resolve(marketId, o, resultData, metadata);
	}

	function redeem() external nonReentrant {
		if (status != Status.Resolved) revert Errors.MarketNotResolved();
		uint256 payout = PayoutCalculator.computeBinary(outcome, longOf[msg.sender], shortOf[msg.sender]);
		if (payout == 0) revert Errors.InvalidParameter();
		longOf[msg.sender] = 0;
		shortOf[msg.sender] = 0;
		vault.withdrawFromTo(msg.sender, msg.sender, payout);
		emit Redeem(msg.sender, payout);
	}

	// Admin hooks (factory-only)
	function updateFlatFee(uint256 newFee) external onlyFactory {
		flatFee = newFee;
	}

	function updateFeeRecipient(address newRecipient) external onlyFactory {
		if (newRecipient == address(0)) revert Errors.ZeroAddress();
		feeRecipient = newRecipient;
	}
}

contracts/market/MarketFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Errors} from "../security/Errors.sol";
import {Market} from "./Market.sol";
import {Vault} from "./Vault.sol";

contract MarketFactory {
	address public admin;
	address public oracle;
	address public feeRecipient;
	uint256 public flatFee; // in collateral units

	event MarketDeployed(bytes32 indexed marketId, address market, address vault, string question, uint256 endTime, uint16 feeBps, uint256 flatFee, address feeRecipient);
	event AdminUpdated(address admin);
	event OracleUpdated(address oracle);
	event FeeRecipientUpdated(address recipient);
	event FlatFeeUpdated(uint256 amount);

	modifier onlyAdmin() {
		if (msg.sender != admin) revert Errors.OnlyAdmin();
		_;
	}

	constructor(address admin_, address oracle_, address feeRecipient_, uint256 flatFee_) {
		if (admin_ == address(0) || oracle_ == address(0) || feeRecipient_ == address(0)) revert Errors.ZeroAddress();
		admin = admin_;
		oracle = oracle_;
		feeRecipient = feeRecipient_;
		flatFee = flatFee_;
	}

	function setOracle(address oracle_) external onlyAdmin {
		if (oracle_ == address(0)) revert Errors.ZeroAddress();
		oracle = oracle_;
		emit OracleUpdated(oracle_);
	}

	function setAdmin(address admin_) external onlyAdmin {
		if (admin_ == address(0)) revert Errors.ZeroAddress();
		admin = admin_;
		emit AdminUpdated(admin_);
	}

	function setFeeRecipient(address recipient) external onlyAdmin {
		if (recipient == address(0)) revert Errors.ZeroAddress();
		feeRecipient = recipient;
		emit FeeRecipientUpdated(recipient);
	}

	function setFlatFee(uint256 amount) external onlyAdmin {
		flatFee = amount;
		emit FlatFeeUpdated(amount);
	}

	function computeMarketId(address creator, string memory question, uint256 endTime) public pure returns (bytes32) {
		return keccak256(abi.encode(creator, question, endTime));
	}

	function createMarket(
		address collateral,
		string memory question,
		uint256 endTime,
		uint16 feeBps
	) external returns (address market, address vault) {
		return createMarketWithOracle(collateral, question, endTime, feeBps, oracle);
	}

	/// @notice Create a market with a specific oracle (or use factory default if address(0))
	/// @param collateral_ The collateral token address
	/// @param question_ The market question
	/// @param endTime_ The trading end time
	/// @param feeBps_ The fee basis points
	/// @param oracle_ The oracle address (address(0) uses factory default)
	function createMarketWithOracle(
		address collateral_,
		string memory question_,
		uint256 endTime_,
		uint16 feeBps_,
		address oracle_
	) public returns (address market, address vault) {
		if (collateral_ == address(0)) revert Errors.ZeroAddress();
		if (bytes(question_).length == 0) revert Errors.InvalidParameter();
		if (endTime_ <= block.timestamp) revert Errors.InvalidTime();
		if (feeBps_ > 10_000) revert Errors.InvalidFee();

		// Use provided oracle or factory default
		address marketOracle = oracle_ == address(0) ? oracle : oracle_;
		if (marketOracle == address(0)) revert Errors.ZeroAddress();

		bytes32 marketId = computeMarketId(msg.sender, question_, endTime_);

		vault = address(new Vault(collateral_));
		market = address(new Market(collateral_, marketOracle, address(this), vault, marketId, question_, endTime_, feeBps_, flatFee, feeRecipient));
		Vault(vault).setMarket(market);

		emit MarketDeployed(marketId, market, vault, question_, endTime_, feeBps_, flatFee, feeRecipient);
	}
}

contracts/market/Vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Errors} from "../security/Errors.sol";

/// @notice Escrows collateral for a single market.
contract Vault {
	IERC20 public immutable collateral;
	address public market; // controller set once by factory
	bool private _marketSet;

	mapping(address => uint256) public collateralOf;

	modifier onlyMarket() {
		if (msg.sender != market) revert Errors.OnlyMarket();
		_;
	}

	constructor(address collateral_) {
		if (collateral_ == address(0)) revert Errors.ZeroAddress();
		collateral = IERC20(collateral_);
	}

	function setMarket(address market_) external {
		if (_marketSet) revert Errors.InvalidParameter();
		if (market_ == address(0)) revert Errors.ZeroAddress();
		market = market_;
		_marketSet = true;
	}

	function depositFrom(address payer, uint256 amount) external onlyMarket {
		if (amount == 0) revert Errors.InvalidParameter();
		(bool s) = collateral.transferFrom(payer, address(this), amount);
		if (!s) revert Errors.TransferFailed();
		collateralOf[payer] += amount;
	}

	function withdrawFromTo(address from, address to, uint256 amount) external onlyMarket {
		if (amount == 0) revert Errors.InvalidParameter();
		uint256 bal = collateralOf[from];
		if (bal < amount) revert Errors.InsufficientCollateral();
		collateralOf[from] = bal - amount;
		(bool s) = collateral.transfer(to, amount);
		if (!s) revert Errors.TransferFailed();
	}

	function sweepTo(address to, uint256 amount) external onlyMarket {
		(bool s) = collateral.transfer(to, amount);
		if (!s) revert Errors.TransferFailed();
	}
}

