// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IGnosisConditionalTokens} from "../interfaces/IGnosisConditionalTokens.sol";

/// @title Mock Gnosis Conditional Tokens
/// @notice Mock implementation for testing GnosisAdapter
contract MockGnosisConditionalTokens is IGnosisConditionalTokens {
	struct ConditionData {
		address oracle;
		bytes32 questionId;
		uint256 outcomeSlotCount;
		bool resolved;
		uint256[] payouts;
	}

	mapping(bytes32 => ConditionData) public conditions;

	/// @notice Prepare a condition for a market
	function prepareCondition(
		address oracle,
		bytes32 questionId,
		uint256 outcomeSlotCount
	) external override returns (bytes32 conditionId) {
		conditionId = keccak256(abi.encodePacked(oracle, questionId, outcomeSlotCount, block.timestamp));

		conditions[conditionId] = ConditionData({
			oracle: oracle,
			questionId: questionId,
			outcomeSlotCount: outcomeSlotCount,
			resolved: false,
			payouts: new uint256[](0)
		});

		emit ConditionPreparation(conditionId, oracle, questionId, outcomeSlotCount);
	}

	/// @notice Report payouts for a condition (resolve it)
	function reportPayouts(bytes32 conditionId, uint256[] calldata payouts) external override {
		ConditionData storage c = conditions[conditionId];
		require(c.oracle != address(0), "Condition not found");
		require(!c.resolved, "Already resolved");
		require(payouts.length == c.outcomeSlotCount, "Invalid payout length");

		c.resolved = true;
		c.payouts = payouts;

		emit PayoutReported(conditionId, payouts);
	}

	/// @notice Get payout for a specific position
	function getPayout(bytes32 conditionId, uint256 outcomeSlot) external view override returns (uint256) {
		ConditionData memory c = conditions[conditionId];
		if (!c.resolved || outcomeSlot >= c.payouts.length) {
			return 0;
		}
		return c.payouts[outcomeSlot];
	}

	/// @notice Check if a condition is resolved
	function getConditionPayouts(bytes32 conditionId)
		external
		view
		override
		returns (bool resolved, uint256[] memory payouts)
	{
		ConditionData memory c = conditions[conditionId];
		return (c.resolved, c.payouts);
	}
}

contracts/test/MockUMAOptimisticOracle.s.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IUMAOptimisticOracle} from "../interfaces/IUMAOptimisticOracle.sol";

/// @title Mock UMA Optimistic Oracle
/// @notice Mock implementation for testing UMAAdapter
contract MockUMAOptimisticOracle is IUMAOptimisticOracle {
	struct AssertionData {
		address asserter;
		bytes claim;
		address callbackRecipient;
		address escalationManager;
		uint64 liveness;
		address currency;
		uint256 bond;
		bytes32 identifier;
		bool settled;
		bool settlementResolution;
	}

	mapping(bytes32 => AssertionData) public assertions;

	/// @notice Assert truth about a claim
	function assertTruth(
		bytes memory claim,
		address asserter,
		address callbackRecipient,
		address escalationManager,
		uint64 liveness,
		address currency,
		uint256 bond,
		bytes32 identifier
	) external override returns (bytes32 assertionId) {
		assertionId = keccak256(abi.encodePacked(claim, asserter, block.timestamp, block.prevrandao));

		assertions[assertionId] = AssertionData({
			asserter: asserter,
			claim: claim,
			callbackRecipient: callbackRecipient,
			escalationManager: escalationManager,
			liveness: liveness,
			currency: currency,
			bond: bond,
			identifier: identifier,
			settled: false,
			settlementResolution: false
		});

		emit AssertionMade(
			assertionId,
			claim,
			asserter,
			callbackRecipient,
			escalationManager,
			liveness,
			currency,
			bond,
			identifier
		);
	}

	/// @notice Get assertion data
	function getAssertion(bytes32 assertionId)
		external
		view
		override
		returns (
			address asserter,
			bytes memory claim,
			address callbackRecipient,
			address escalationManager,
			uint64 liveness,
			address currency,
			uint256 bond,
			bytes32 identifier,
			bool settled,
			bool settlementResolution
		)
	{
		AssertionData memory a = assertions[assertionId];
		return (
			a.asserter,
			a.claim,
			a.callbackRecipient,
			a.escalationManager,
			a.liveness,
			a.currency,
			a.bond,
			a.identifier,
			a.settled,
			a.settlementResolution
		);
	}

	/// @notice Settle an assertion (for testing)
	function settleAssertion(bytes32 assertionId, bool resolution) external {
		AssertionData storage a = assertions[assertionId];
		require(a.asserter != address(0), "Assertion not found");
		require(!a.settled, "Already settled");

		a.settled = true;
		a.settlementResolution = resolution;

		emit AssertionSettled(assertionId, resolution);
	}
}

