contracts/adapter/GnosisAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IVPOAdapter} from "../interfaces/IVPOAdapter.sol";
import {IGnosisConditionalTokens} from "../interfaces/IGnosisConditionalTokens.sol";
import {Errors} from "../security/Errors.sol";

/// @title Gnosis Adapter
/// @notice Bridges Gnosis Conditional Tokens with VPOAdapter for verifiable outcomes
/// @dev Listens to Gnosis condition events, requests verification from VPOAdapter, and sets outcomes
contract GnosisAdapter {
	/// @notice The VPOAdapter contract
	IVPOAdapter public immutable vpoAdapter;

	/// @notice The Gnosis ConditionalTokens contract
	IGnosisConditionalTokens public immutable conditionalTokens;

	/// @notice Admin address
	address public admin;

	/// @notice Mapping from condition ID to VPOAdapter request ID
	mapping(bytes32 => bytes32) public conditionToRequest;

	/// @notice Mapping from VPOAdapter request ID to condition ID
	mapping(bytes32 => bytes32) public requestToCondition;

	/// @notice Mapping to track if a condition has been resolved
	mapping(bytes32 => bool) public conditionResolved;

	modifier onlyAdmin() {
		if (msg.sender != admin) revert Errors.OnlyAdmin();
		_;
	}

	constructor(
		address vpoAdapter_,
		address conditionalTokens_,
		address admin_
	) {
		if (vpoAdapter_ == address(0)) revert Errors.ZeroAddress();
		if (conditionalTokens_ == address(0)) revert Errors.ZeroAddress();
		if (admin_ == address(0)) revert Errors.ZeroAddress();

		vpoAdapter = IVPOAdapter(vpoAdapter_);
		conditionalTokens = IGnosisConditionalTokens(conditionalTokens_);
		admin = admin_;
	}

	/// @notice Handle a new Gnosis condition by requesting verification from VPOAdapter
	/// @param conditionId The Gnosis condition ID
	/// @param questionId The question ID (bytes32 identifier)
	/// @param outcomeSlotCount The number of outcome slots
	/// @param data Additional data for VPOAdapter (data sources, timestamps, etc.)
	/// @return requestId The VPOAdapter request ID
	function handleCondition(
		bytes32 conditionId,
		bytes32 questionId,
		uint256 outcomeSlotCount,
		bytes calldata data
	) external returns (bytes32 requestId) {
		// Check condition is not already resolved
		(bool resolved, ) = conditionalTokens.getConditionPayouts(conditionId);
		if (resolved) revert Errors.AlreadyFulfilled();

		if (conditionToRequest[conditionId] != bytes32(0)) {
			revert Errors.AlreadyFulfilled(); // Already handled
		}

		// Request verification from VPOAdapter
		// Use conditionId as marketRef
		requestId = vpoAdapter.requestVerification(conditionId, data);

		// Store mapping
		conditionToRequest[conditionId] = requestId;
		requestToCondition[requestId] = conditionId;

		emit ConditionHandled(conditionId, requestId, questionId, outcomeSlotCount);
	}

	/// @notice Resolve a condition after VPOAdapter fulfillment
	/// @param requestId The VPOAdapter request ID
	/// @param outcomeSlotCount The number of outcome slots (must match condition)
	/// @return conditionId The Gnosis condition ID
	function resolveCondition(
		bytes32 requestId,
		uint256 outcomeSlotCount
	) external onlyAdmin returns (bytes32 conditionId) {
		// Get the condition ID
		conditionId = requestToCondition[requestId];
		if (conditionId == bytes32(0)) revert Errors.NotFound();

		// Check VPOAdapter fulfillment
		(bool exists, , bool outcome, ) = vpoAdapter.getFulfillment(requestId);
		if (!exists) revert Errors.NotFound();

		// Check if already resolved
		if (conditionResolved[conditionId]) revert Errors.AlreadyFulfilled();

		// Verify condition is not already resolved on Gnosis side
		(bool resolved, ) = conditionalTokens.getConditionPayouts(conditionId);
		if (resolved) {
			conditionResolved[conditionId] = true;
			emit ConditionResolved(conditionId, requestId, outcome);
			return conditionId;
		}

		// Create payouts array
		// For binary markets: outcomeSlotCount should be 2
		// outcome = true means slot 0 wins (YES), outcome = false means slot 1 wins (NO)
		uint256[] memory payouts = new uint256[](outcomeSlotCount);

		if (outcome) {
			// YES wins: slot 0 gets full payout
			payouts[0] = 1;
			for (uint256 i = 1; i < outcomeSlotCount; i++) {
				payouts[i] = 0;
			}
		} else {
			// NO wins: slot 1 gets full payout (or last slot if more than 2)
			payouts[0] = 0;
			if (outcomeSlotCount > 1) {
				payouts[outcomeSlotCount - 1] = 1;
				for (uint256 i = 1; i < outcomeSlotCount - 1; i++) {
					payouts[i] = 0;
				}
			}
		}

		// Report payouts to Gnosis
		conditionalTokens.reportPayouts(conditionId, payouts);

		// Mark as resolved
		conditionResolved[conditionId] = true;

		emit ConditionResolved(conditionId, requestId, outcome);
	}

	/// @notice Get the VPOAdapter request ID for a Gnosis condition
	/// @param conditionId The Gnosis condition ID
	/// @return requestId The VPOAdapter request ID (bytes32(0) if not found)
	function getRequestId(bytes32 conditionId) external view returns (bytes32 requestId) {
		return conditionToRequest[conditionId];
	}

	/// @notice Get the Gnosis condition ID for a VPOAdapter request
	/// @param requestId The VPOAdapter request ID
	/// @return conditionId The Gnosis condition ID (bytes32(0) if not found)
	function getConditionId(bytes32 requestId) external view returns (bytes32 conditionId) {
		return requestToCondition[requestId];
	}

	/// @notice Event emitted when a Gnosis condition is handled
	event ConditionHandled(
		bytes32 indexed conditionId,
		bytes32 indexed requestId,
		bytes32 questionId,
		uint256 outcomeSlotCount
	);

	/// @notice Event emitted when a condition is resolved
	event ConditionResolved(
		bytes32 indexed conditionId,
		bytes32 indexed requestId,
		bool outcome
	);
}

contracts/adapter/UMAAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IVPOAdapter} from "../interfaces/IVPOAdapter.sol";
import {IUMAOptimisticOracle} from "../interfaces/IUMAOptimisticOracle.sol";
import {Errors} from "../security/Errors.sol";

/// @title UMA Adapter
/// @notice Bridges UMA Optimistic Oracle assertions with VPOAdapter for verifiable outcomes
/// @dev Listens to UMA assertions, requests verification from VPOAdapter, and submits outcomes back to UMA
contract UMAAdapter {
	/// @notice The VPOAdapter contract
	IVPOAdapter public immutable vpoAdapter;

	/// @notice The UMA Optimistic Oracle contract
	IUMAOptimisticOracle public immutable umaOracle;

	/// @notice Admin address
	address public admin;

	/// @notice Mapping from UMA assertion ID to VPOAdapter request ID
	mapping(bytes32 => bytes32) public assertionToRequest;

	/// @notice Mapping from VPOAdapter request ID to UMA assertion ID
	mapping(bytes32 => bytes32) public requestToAssertion;

	/// @notice Mapping to track if an assertion has been submitted to UMA
	mapping(bytes32 => bool) public assertionSubmitted;

	modifier onlyAdmin() {
		if (msg.sender != admin) revert Errors.OnlyAdmin();
		_;
	}

	constructor(address vpoAdapter_, address umaOracle_, address admin_) {
		if (vpoAdapter_ == address(0)) revert Errors.ZeroAddress();
		if (umaOracle_ == address(0)) revert Errors.ZeroAddress();
		if (admin_ == address(0)) revert Errors.ZeroAddress();

		vpoAdapter = IVPOAdapter(vpoAdapter_);
		umaOracle = IUMAOptimisticOracle(umaOracle_);
		admin = admin_;
	}

	/// @notice Handle a new UMA assertion by requesting verification from VPOAdapter
	/// @param assertionId The UMA assertion ID
	/// @param claim The claim being asserted (encoded question/data)
	/// @param data Additional data for VPOAdapter (data sources, timestamps, etc.)
	/// @return requestId The VPOAdapter request ID
	function handleAssertion(
		bytes32 assertionId,
		bytes calldata claim,
		bytes calldata data
	) external returns (bytes32 requestId) {
		// Check assertion exists and is not already handled
		(, , , , , , , , bool settled, ) = umaOracle.getAssertion(assertionId);
		if (settled) revert Errors.AlreadyFulfilled();

		if (assertionToRequest[assertionId] != bytes32(0)) {
			revert Errors.AlreadyFulfilled(); // Already handled
		}

		// Request verification from VPOAdapter
		// Use assertionId as marketRef
		requestId = vpoAdapter.requestVerification(assertionId, data);

		// Store mapping
		assertionToRequest[assertionId] = requestId;
		requestToAssertion[requestId] = assertionId;

		emit AssertionHandled(assertionId, requestId, claim);
	}

	/// @notice Submit verified outcome to UMA after VPOAdapter fulfillment
	/// @param requestId The VPOAdapter request ID
	/// @param claim The claim to assert (should match original UMA assertion claim)
	/// @param liveness The liveness period for disputes (in seconds)
	/// @param currency The currency for bonds (address(0) for native)
	/// @param bond The bond amount (can be 0)
	/// @param identifier The identifier for the assertion
	/// @return assertionId The UMA assertion ID (may be new or existing)
	function submitOutcomeToUMA(
		bytes32 requestId,
		bytes calldata claim,
		uint64 liveness,
		address currency,
		uint256 bond,
		bytes32 identifier
	) external onlyAdmin returns (bytes32 assertionId) {
		// Get the original assertion ID
		assertionId = requestToAssertion[requestId];
		if (assertionId == bytes32(0)) revert Errors.NotFound();

		// Check VPOAdapter fulfillment
		(bool exists, , bool outcome, ) = vpoAdapter.getFulfillment(requestId);
		if (!exists) revert Errors.NotFound();

		// Check if already submitted
		if (assertionSubmitted[assertionId]) revert Errors.AlreadyFulfilled();

		// Check if original assertion is already settled
		bool settled = _isAssertionSettled(assertionId);
		if (settled) {
			bool settlementResolution = _getSettlementResolution(assertionId);
			assertionSubmitted[assertionId] = true;
			emit OutcomeSubmitted(assertionId, requestId, settlementResolution);
			return assertionId;
		}

		// Create new assertion with verified outcome
		bytes memory outcomeClaim = abi.encode(claim, outcome);
		bytes32 newAssertionId = _createUMAAssertion(
			outcomeClaim,
			liveness,
			currency,
			bond,
			identifier
		);

		// Mark as submitted
		assertionSubmitted[assertionId] = true;

		emit OutcomeSubmitted(assertionId, requestId, outcome);
		emit UMAAssertionCreated(newAssertionId, assertionId, outcome);
	}

	/// @dev Check if an assertion is settled
	function _isAssertionSettled(bytes32 assertionId) internal view returns (bool) {
		(, , , , , , , , bool settled, ) = umaOracle.getAssertion(assertionId);
		return settled;
	}

	/// @dev Get settlement resolution
	function _getSettlementResolution(bytes32 assertionId) internal view returns (bool) {
		(, , , , , , , , , bool settlementResolution) = umaOracle.getAssertion(assertionId);
		return settlementResolution;
	}

	/// @dev Create UMA assertion
	function _createUMAAssertion(
		bytes memory outcomeClaim,
		uint64 liveness,
		address currency,
		uint256 bond,
		bytes32 identifier
	) internal returns (bytes32) {
		return umaOracle.assertTruth(
			outcomeClaim,
			address(this), // asserter
			address(0), // callbackRecipient
			address(0), // escalationManager
			liveness,
			currency,
			bond,
			identifier
		);
	}

	/// @notice Get the VPOAdapter request ID for a UMA assertion
	/// @param assertionId The UMA assertion ID
	/// @return requestId The VPOAdapter request ID (bytes32(0) if not found)
	function getRequestId(bytes32 assertionId) external view returns (bytes32 requestId) {
		return assertionToRequest[assertionId];
	}

	/// @notice Get the UMA assertion ID for a VPOAdapter request
	/// @param requestId The VPOAdapter request ID
	/// @return assertionId The UMA assertion ID (bytes32(0) if not found)
	function getAssertionId(bytes32 requestId) external view returns (bytes32 assertionId) {
		return requestToAssertion[requestId];
	}

	/// @notice Event emitted when a UMA assertion is handled
	event AssertionHandled(
		bytes32 indexed assertionId,
		bytes32 indexed requestId,
		bytes claim
	);

	/// @notice Event emitted when an outcome is submitted to UMA
	event OutcomeSubmitted(
		bytes32 indexed assertionId,
		bytes32 indexed requestId,
		bool outcome
	);

	/// @notice Event emitted when a new UMA assertion is created
	event UMAAssertionCreated(
		bytes32 indexed newAssertionId,
		bytes32 indexed originalAssertionId,
		bool outcome
	);
}

contracts/adapter/VPOAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import {IVPOAdapter} from "../interfaces/IVPOAdapter.sol";
import {Errors} from "../security/Errors.sol";

/// @title VPO Adapter
/// @notice On-chain adapter that receives verification requests from external prediction markets
/// and coordinates with EigenCloud AVS nodes to provide verifiable outcomes.
/// Implements quorum consensus requiring ≥⅔ operator agreement before finalization.
contract VPOAdapter is IVPOAdapter {
	// Internal structs (must be defined before use)
	struct Request {
		bytes32 marketRef;
		address requester;
		bytes data;
		bool fulfilled;
		bytes attestationCid;
		bool outcome;
		bytes metadata;
	}

	struct Attestation {
		address operator;
		bool outcome;
		bytes attestationCid;
		bytes signature;
		uint256 timestamp;
	}

	/// @dev requestId => Request
	mapping(bytes32 => Request) private _requests;

	/// @dev AVS nodes that are authorized to fulfill requests
	mapping(address => bool) public avsNodes;

	/// @dev Operator weights (stakes) for quorum calculation
	mapping(address => uint256) public operatorWeights;

	/// @dev Total weight of all registered operators
	uint256 public totalOperatorWeight;

	/// @dev requestId => Attestation[]
	mapping(bytes32 => Attestation[]) private _attestations;

	/// @dev requestId => outcome => total weight attested
	mapping(bytes32 => mapping(bool => uint256)) private _outcomeWeights;

	/// @dev Quorum threshold as percentage (default 66 = 66%)
	uint256 public quorumThreshold = 66;

	address public admin;

	modifier onlyAdmin() {
		if (msg.sender != admin) revert Errors.OnlyAdmin();
		_;
	}

	modifier onlyAVS() {
		if (!avsNodes[msg.sender]) revert Errors.Unauthorized();
		_;
	}

	constructor(address admin_) {
		if (admin_ == address(0)) revert Errors.ZeroAddress();
		admin = admin_;
	}

	/// @notice Add or remove an AVS node
	function setAVSNode(address node, bool enabled) external onlyAdmin {
		if (node == address(0)) revert Errors.ZeroAddress();
		avsNodes[node] = enabled;
		emit AVSNodeUpdated(node, enabled);
	}

	/// @notice Set operator weight (stake) for quorum calculation
	/// @param operator The operator address
	/// @param weight The weight/stake amount
	function setOperatorWeight(address operator, uint256 weight) external onlyAdmin {
		if (operator == address(0)) revert Errors.ZeroAddress();

		uint256 oldWeight = operatorWeights[operator];
		operatorWeights[operator] = weight;

		// Update total weight
		totalOperatorWeight = totalOperatorWeight - oldWeight + weight;

		emit OperatorWeightUpdated(operator, weight);
	}

	/// @notice Set quorum threshold percentage (e.g., 66 = 66%)
	function setQuorumThreshold(uint256 threshold) external onlyAdmin {
		if (threshold > 100) revert Errors.InvalidParameter();
		quorumThreshold = threshold;
		emit QuorumThresholdUpdated(threshold);
	}

	/// @notice Request verification for an external market
	/// @param marketRef Identifier from external market (e.g., UMA dispute id, conditional question id)
	/// @param data Encoded parameters (data sources, timestamps, query details)
	/// @return requestId Unique identifier for this request
	function requestVerification(bytes32 marketRef, bytes calldata data)
		external
		override
		returns (bytes32 requestId)
	{
		// Generate unique request ID
		requestId = keccak256(abi.encodePacked(marketRef, msg.sender, block.timestamp, data, block.prevrandao));

		// Store request
		_requests[requestId] = Request({
			marketRef: marketRef,
			requester: msg.sender,
			data: data,
			fulfilled: false,
			attestationCid: "",
			outcome: false,
			metadata: ""
		});

		emit VerificationRequested(requestId, msg.sender, marketRef, data);
	}

	/// @notice Internal function to submit an attestation
	function _submitAttestationInternal(
		bytes32 requestId,
		bool outcome,
		bytes calldata attestationCid,
		bytes calldata signature,
		address operator
	) internal {
		Request storage req = _requests[requestId];

		// Check request exists
		if (req.requester == address(0)) revert Errors.NotFound();

		// Check not already fulfilled
		if (req.fulfilled) revert Errors.AlreadyFulfilled();

		// Check operator has weight
		uint256 weight = operatorWeights[operator];
		if (weight == 0) revert Errors.Unauthorized();

		// Check operator hasn't already attested
		for (uint256 i = 0; i < _attestations[requestId].length; i++) {
			if (_attestations[requestId][i].operator == operator) {
				revert Errors.AlreadyFulfilled(); // Reuse error for "already attested"
			}
		}

		// Add attestation
		_attestations[requestId].push(Attestation({
			operator: operator,
			outcome: outcome,
			attestationCid: attestationCid,
			signature: signature,
			timestamp: block.timestamp
		}));

		// Update outcome weight
		_outcomeWeights[requestId][outcome] += weight;

		emit AttestationSubmitted(requestId, operator, outcome, attestationCid, signature);

		// Check if quorum reached for this outcome
		uint256 outcomeWeight = _outcomeWeights[requestId][outcome];
		uint256 requiredWeight = (totalOperatorWeight * quorumThreshold) / 100;

		if (outcomeWeight >= requiredWeight && totalOperatorWeight > 0) {
			emit QuorumReached(requestId, outcome, outcomeWeight);
		}
	}

	/// @notice Submit an attestation for a verification request (quorum-based)
	/// @param requestId The request ID from requestVerification
	/// @param outcome Resolved boolean outcome (true = YES, false = NO)
	/// @param attestationCid IPFS CID (as bytes) to the public proof payload
	/// @param signature Operator's signature on the attestation
	function submitAttestation(
		bytes32 requestId,
		bool outcome,
		bytes calldata attestationCid,
		bytes calldata signature
	) external override onlyAVS {
		_submitAttestationInternal(requestId, outcome, attestationCid, signature, msg.sender);
	}

	/// @notice Fulfill a verification request with attestation and outcome (legacy - now checks quorum)
	/// @param requestId The request ID from requestVerification
	/// @param attestationCid IPFS CID (as bytes) to the public proof payload
	/// @param outcome Resolved boolean outcome (true = YES, false = NO)
	/// @param metadata Provider-specific metadata (timestamps, signatures, etc.)
	function fulfillVerification(
		bytes32 requestId,
		bytes calldata attestationCid,
		bool outcome,
		bytes calldata metadata
	) external override onlyAVS {
		// For backward compatibility, treat as attestation submission
		// Use metadata as signature (or empty bytes if not provided)
		_submitAttestationInternal(requestId, outcome, attestationCid, metadata, msg.sender);

		// If quorum reached, automatically finalize
		if (_isQuorumReached(requestId, outcome)) {
			_finalizeResolutionInternal(requestId, outcome, "");
		}
	}

	/// @notice Internal function to finalize resolution
	function _finalizeResolutionInternal(
		bytes32 requestId,
		bool outcome,
		bytes memory aggregateSignature
	) internal {
		Request storage req = _requests[requestId];

		// Check request exists
		if (req.requester == address(0)) revert Errors.NotFound();

		// Check not already fulfilled
		if (req.fulfilled) revert Errors.AlreadyFulfilled();

		// Check quorum reached
		if (!_isQuorumReached(requestId, outcome)) revert Errors.InvalidParameter();

		// Aggregate attestation CIDs (use first one for now, can improve later)
		bytes memory finalAttestationCid = "";
		if (_attestations[requestId].length > 0) {
			finalAttestationCid = _attestations[requestId][0].attestationCid;
		}

		// Update request
		req.fulfilled = true;
		req.attestationCid = finalAttestationCid;
		req.outcome = outcome;
		req.metadata = aggregateSignature;

		emit ResolutionFinalized(requestId, outcome, aggregateSignature, _outcomeWeights[requestId][outcome]);
		emit VerificationFulfilled(requestId, finalAttestationCid, outcome, aggregateSignature);
	}

	/// @notice Finalize resolution after quorum is reached
	/// @param requestId The request ID
	/// @param outcome The outcome that reached quorum
	/// @param aggregateSignature Aggregated signature from all operators (can be empty for now)
	function finalizeResolution(
		bytes32 requestId,
		bool outcome,
		bytes calldata aggregateSignature
	) public override {
		_finalizeResolutionInternal(requestId, outcome, aggregateSignature);
	}

	/// @notice Check if quorum is reached for a specific outcome
	function _isQuorumReached(bytes32 requestId, bool outcome) internal view returns (bool) {
		if (totalOperatorWeight == 0) return false;

		uint256 outcomeWeight = _outcomeWeights[requestId][outcome];
		uint256 requiredWeight = (totalOperatorWeight * quorumThreshold) / 100;

		return outcomeWeight >= requiredWeight;
	}

	/// @notice Read back fulfillment if present
	/// @param requestId The request ID to check
	/// @return exists Whether fulfillment exists
	/// @return attestationCid IPFS CID bytes
	/// @return outcome Resolved boolean
	/// @return metadata Opaque metadata
	function getFulfillment(bytes32 requestId)
		external
		view
		override
		returns (bool exists, bytes memory attestationCid, bool outcome, bytes memory metadata)
	{
		Request memory req = _requests[requestId];

		if (req.requester == address(0)) {
			return (false, "", false, "");
		}

		if (!req.fulfilled) {
			return (false, "", false, "");
		}

		return (true, req.attestationCid, req.outcome, req.metadata);
	}

	/// @notice Get request details (for debugging/verification)
	function getRequest(bytes32 requestId) external view returns (Request memory) {
		return _requests[requestId];
	}

	/// @notice Get all attestations for a request
	function getAttestations(bytes32 requestId) external view returns (Attestation[] memory) {
		return _attestations[requestId];
	}

	/// @notice Get quorum status for a request
	/// @return isQuorumReached Whether quorum is reached
	/// @return yesWeight Total weight for YES outcome
	/// @return noWeight Total weight for NO outcome
	/// @return requiredWeight Weight needed for quorum
	function getQuorumStatus(bytes32 requestId) external view returns (
		bool isQuorumReached,
		uint256 yesWeight,
		uint256 noWeight,
		uint256 requiredWeight
	) {
		requiredWeight = (totalOperatorWeight * quorumThreshold) / 100;
		yesWeight = _outcomeWeights[requestId][true];
		noWeight = _outcomeWeights[requestId][false];
		isQuorumReached = (yesWeight >= requiredWeight || noWeight >= requiredWeight) && totalOperatorWeight > 0;
	}

	/// @notice Event emitted when AVS node is added/removed
	event AVSNodeUpdated(address indexed node, bool enabled);

	/// @notice Event emitted when operator weight is updated
	event OperatorWeightUpdated(address indexed operator, uint256 weight);

	/// @notice Event emitted when quorum threshold is updated
	event QuorumThresholdUpdated(uint256 threshold);
}

contracts/interfaces/IGnosisConditionalT.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

/// @notice Simplified interface for Gnosis Conditional Tokens
/// @dev Based on Gnosis ConditionalTokens contract
interface IGnosisConditionalTokens {
	/// @notice Prepare a condition for a market
	/// @param oracle The oracle address that will resolve the condition
	/// @param questionId The question ID (bytes32 identifier)
	/// @param outcomeSlotCount The number of outcome slots
	/// @return conditionId The unique condition ID
	function prepareCondition(
		address oracle,
		bytes32 questionId,
		uint256 outcomeSlotCount
	) external returns (bytes32 conditionId);

	/// @notice Report payouts for a condition (resolve it)
	/// @param conditionId The condition ID
	/// @param payouts Array of payout amounts for each outcome slot
	function reportPayouts(bytes32 conditionId, uint256[] calldata payouts) external;

	/// @notice Get payout for a specific position
	/// @param conditionId The condition ID
	/// @param outcomeSlot The outcome slot index
	/// @return payout The payout amount for this outcome
	function getPayout(bytes32 conditionId, uint256 outcomeSlot) external view returns (uint256);

	/// @notice Check if a condition is resolved
	/// @param conditionId The condition ID
	/// @return resolved Whether the condition is resolved
	/// @return payouts Array of payout amounts (if resolved)
	function getConditionPayouts(bytes32 conditionId)
		external
		view
		returns (bool resolved, uint256[] memory payouts);

	/// @notice Event emitted when a condition is prepared
	event ConditionPreparation(
		bytes32 indexed conditionId,
		address indexed oracle,
		bytes32 indexed questionId,
		uint256 outcomeSlotCount
	);

	/// @notice Event emitted when payouts are reported
	event PayoutReported(
		bytes32 indexed conditionId,
		uint256[] payouts
	);
}

contracts/interfaces/IUMAOptimisticOracl.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

/// @notice Simplified interface for UMA Optimistic Oracle V3
/// @dev Based on UMA's OptimisticOracleV3 contract
interface IUMAOptimisticOracle {
	/// @notice Assert truth about a claim
	/// @param claim The claim being asserted
	/// @param asserter The address making the assertion
	/// @param callbackRecipient The recipient of the callback (if any)
	/// @param escalationManager The escalation manager (if any)
	/// @param liveness The liveness period for disputes
	/// @param currency The currency for bonds
	/// @param bond The bond amount
	/// @param identifier The identifier for the assertion
	/// @return assertionId The unique identifier for this assertion
	function assertTruth(
		bytes memory claim,
		address asserter,
		address callbackRecipient,
		address escalationManager,
		uint64 liveness,
		address currency,
		uint256 bond,
		bytes32 identifier
	) external returns (bytes32 assertionId);

	/// @notice Get assertion data
	/// @param assertionId The assertion ID
	/// @return asserter The address that made the assertion
	/// @return claim The claim being asserted
	/// @return callbackRecipient The callback recipient
	/// @return escalationManager The escalation manager
	/// @return liveness The liveness period
	/// @return currency The currency
	/// @return bond The bond amount
	/// @return identifier The identifier
	/// @return settled Whether the assertion is settled
	/// @return settlementResolution The resolution (if settled)
	function getAssertion(bytes32 assertionId)
		external
		view
		returns (
			address asserter,
			bytes memory claim,
			address callbackRecipient,
			address escalationManager,
			uint64 liveness,
			address currency,
			uint256 bond,
			bytes32 identifier,
			bool settled,
			bool settlementResolution
		);

	/// @notice Event emitted when an assertion is made
	event AssertionMade(
		bytes32 indexed assertionId,
		bytes claim,
		address indexed asserter,
		address callbackRecipient,
		address escalationManager,
		uint64 liveness,
		address currency,
		uint256 bond,
		bytes32 identifier
	);

	/// @notice Event emitted when an assertion is settled
	event AssertionSettled(
		bytes32 indexed assertionId,
		bool resolution
	);
}

contracts/interfaces/IVPOAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

/// @notice Interface for the VPO Adapter used by external prediction markets to request verifiable outcomes
/// and receive verified results with attestations (e.g., IPFS CIDs) produced by the AVS.
/// Implements quorum consensus requiring ≥⅔ operator agreement before finalization.
interface IVPOAdapter {
    /// @dev Emitted when a verification request is registered.
    event VerificationRequested(bytes32 indexed requestId, address indexed requester, bytes32 indexed marketRef, bytes data);

    /// @dev Emitted when a verification is fulfilled with an attestation reference and result.
    event VerificationFulfilled(bytes32 indexed requestId, bytes attestationCid, bool outcome, bytes metadata);

    /// @dev Emitted when an operator submits an attestation.
    event AttestationSubmitted(
        bytes32 indexed requestId,
        address indexed operator,
        bool outcome,
        bytes attestationCid,
        bytes signature
    );

    /// @dev Emitted when quorum is reached for an outcome.
    event QuorumReached(bytes32 indexed requestId, bool outcome, uint256 totalWeight);

    /// @dev Emitted when resolution is finalized after quorum.
    event ResolutionFinalized(
        bytes32 indexed requestId,
        bool outcome,
        bytes aggregateSignature,
        uint256 totalWeight
    );

    /// @notice Create a verification request that AVS should process.
    /// @param marketRef An identifier from the external market (e.g., UMA dispute id or conditional question id).
    /// @param data Encoded parameters describing the query (data sources, timestamps, etc.).
    /// @return requestId Unique identifier for this verification request.
    function requestVerification(bytes32 marketRef, bytes calldata data) external returns (bytes32 requestId);

    /// @notice Submit an attestation for a verification request (quorum-based).
    /// @param requestId The request ID from requestVerification.
    /// @param outcome Resolved boolean outcome (true = YES, false = NO).
    /// @param attestationCid IPFS CID (as bytes) to the public proof payload.
    /// @param signature Operator's signature on the attestation.
    function submitAttestation(
        bytes32 requestId,
        bool outcome,
        bytes calldata attestationCid,
        bytes calldata signature
    ) external;

    /// @notice Fulfill a request with an attestation and outcome (legacy - now checks quorum).
    /// @param requestId The id previously returned by requestVerification.
    /// @param attestationCid IPFS CID (bytes form) to the public proof payload.
    /// @param outcome The resolved boolean outcome (example: true = YES, false = NO).
    /// @param metadata Provider-specific metadata (timestamps, signatures, etc.).
    function fulfillVerification(bytes32 requestId, bytes calldata attestationCid, bool outcome, bytes calldata metadata) external;

    /// @notice Finalize resolution after quorum is reached.
    /// @param requestId The request ID.
    /// @param outcome The outcome that reached quorum.
    /// @param aggregateSignature Aggregated signature from all operators.
    function finalizeResolution(
        bytes32 requestId,
        bool outcome,
        bytes calldata aggregateSignature
    ) external;

    /// @notice Read back fulfillment if present.
    /// @return exists Whether a fulfillment exists.
    /// @return attestationCid The IPFS CID bytes.
    /// @return outcome The resolved boolean.
    /// @return metadata Opaque metadata.
    function getFulfillment(bytes32 requestId) external view returns (bool exists, bytes memory attestationCid, bool outcome, bytes memory metadata);
}

contracts/security/Errors.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

library Errors {
	// Access control
	error NotAuthorized();
	error Unauthorized();
	error OnlyAdmin();
	error OnlyMarketManager();
	error OnlyOracleManager();
	error OnlyFactory();
	error OnlyMarket();

	// State
	error MarketAlreadyResolved();
	error MarketNotResolved();
	error TradingClosed();
	error TradingOpen();
	error Paused();
	error NotPaused();

	// Input validation
	error InvalidParameter();
	error InvalidFee();
	error InvalidTime();
	error ZeroAddress();

	// Economic
	error InsufficientBalance();
	error InsufficientCollateral();
	error TransferFailed();

	// Adapter specific
	error NotFound();
	error AlreadyFulfilled();

	// Relayer/Attestation specific
	error Expired();
	error AlreadyUsed();
	error InvalidSignature();
}

